import React, { useEffect, useState } from "react";
import axios from "axios";
import { PayPalButton } from "react-paypal-button-v2";
import { Link, useParams, useNavigate, Redirect } from "react-router-dom";
import {
    Button,
    Row,
    Col,
    ListGroup,
    Card,
    Image,
    Spinner,
  } from "react-bootstrap";import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import Loader from "../components/Loader";
import {
    deliverOrder,
    getOrderDetails,
    payOrder,
    payOrderMidtrans,
    updatePayOrderMidtrans,
} from "../actions/orderActions";
import {
    ORDER_PAY_RESET,
    ORDER_DELIVER_RESET,
    ORDER_DETAILS_RESET,
} from "../constants/orderConstants";
// import { Snap } from 'midtrans-client';

  

const OrderScreen = ({history}) => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const match = useParams();
    const orderId = match.id;

    const [sdkReady, setSdkReady] = useState(false);

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const orderDetails = useSelector((state) => state.orderDetails);
    const { order, loading, error } = orderDetails;

    const orderPay = useSelector((state) => state.orderPay);
    const { loading: loadingPay, success: successPay } = orderPay;

    const orderDeliver = useSelector((state) => state.orderDeliver);
    const { loading: loadingDeliver, success: successDeliver } = orderDeliver;

    const orderMidtrans = useSelector((state) => state.orderMidtrans);
    const {
      loading: loadingPayMidtrans,
      success: successPayMidtrans,
      order: orderPayMidtrans,
    } = orderMidtrans;

    const addDecimals = (num) => {
        return Math.round(num * 100) / 100;
      };
    
    if (!loading) {
        order.itemsPrice = addDecimals(
          order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0)
        );
    }
    
    useEffect(() => {
        if (!userInfo) {
          return history.push("/login");
        }
        if (!order || order._id !== orderId) {
          dispatch(getOrderDetails(orderId));
        }
        // eslint-disable-next-line
    }, [order, orderId, userInfo, history]);
    
    useEffect(() => {
        if (!order || successPay || successDeliver) {
          dispatch({ type: ORDER_PAY_RESET });
          dispatch({ type: ORDER_DELIVER_RESET });
          dispatch({ type: ORDER_DETAILS_RESET });
          dispatch(getOrderDetails(orderId));
        }
    }, [dispatch, order, orderId, successPay, successDeliver]);
    
    useEffect(() => {
        const ajaxGetToken = (transactionData, callback) => {
          var snapToken;
          // Request get token to your server & save result to snapToken variable
          snapToken = orderPayMidtrans.token;
        console.log(snapToken);
          if (snapToken) {
            callback(null, snapToken);
          } else {
            callback(new Error("Failed to fetch snap token"), null);
          }
        };

        const snap = window.snap;
        console.log(snap);
        if (successPayMidtrans) {
            snap.show();
            ajaxGetToken(
              { "order_id": order._id, "gross_amount": 50000 },
              function (error, snapToken) {
                if (error) {
                  snap.hide();
                } else {
                  snap.pay(snapToken, {
                    onSuccess: function (result) {
                      console.log("success");
                      console.log(result);
                      return dispatch(updatePayOrderMidtrans(orderId, result));
                    },
                    onPending: function (result) {
                      console.log("pending");
                      console.log(result);
                      return dispatch(updatePayOrderMidtrans(orderId, result));
                    },
                    onError: function (result) {
                      console.log("error");
                      console.log(result);
                      return dispatch(updatePayOrderMidtrans(orderId, result));
                    },
                    onClose: function () {
                      console.log(
                        "customer closed the popup without finishing the payment"
                      );
                    },
                  });
                }
              }
            );
          
        }
      }, [successPayMidtrans, orderPayMidtrans, order, dispatch, orderId]);

    const successPaymentHandler = (paymentResult) => {
        console.log(paymentResult);
        dispatch(payOrder(orderId, paymentResult));
      };

      const deliverHandler = () => {
        dispatch(deliverOrder(orderId));
      };
    
      const midtransHandler = () => {
        // window.snap.pay('')
        dispatch(payOrderMidtrans(orderId));
      };

    return loading ? (
        <Loader />
    ) : error ? (
        <Message variant="danger">{error}</Message>
    ) : (
        <>
            <h1>Order {order._id}</h1>
            <Row>
                <Col>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Shipping</h2>
                            <p>
                                <strong>Name: </strong> {order.user.name}
                            </p>
                            <p>
                                <strong>Email: </strong>
                                <a href={`mailto:${order.user.email}`}>
                                    {order.user.email}
                                </a>
                            </p>
                            <p>
                                <strong>Address: </strong>
                                {order.shippingAddress.address}{" "}
                                {order.shippingAddress.city},{" "}
                                {order.shippingAddress.postalCode},{" "}
                                {order.shippingAddress.country}
                            </p>
                            {order.isDelivered ? (
                                <Message variant="success">
                                    Delivered on {order.deliveredAt}
                                </Message>
                            ) : (
                                <Message variant="danger">
                                    Not Delivered
                                </Message>
                            )}
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Payment Method</h2>
                            <p>
                                <strong>Method: </strong>
                                {order.paymentMethod}
                            </p>
                            {order.isPaid ? (
                                <Message variant="success">
                                    Paid on {order.paidAt}
                                </Message>
                            ) : (
                                <Message variant="danger">Not Paid</Message>
                            )}
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Order Items</h2>
                            {order.orderItems.length === 0 ? (
                                <Message>Order is empty</Message>
                            ) : (
                                <ListGroup variant="flush">
                                    {order.orderItems.map((item, index) => (
                                        <ListGroup.Item key={index}>
                                            <Row className="align-items-center">
                                                <Col md={2}>
                                                    <Image
                                                        src={item.image}
                                                        alt={item.name}
                                                        fluid
                                                        rounded
                                                    />
                                                </Col>
                                                <Col>
                                                    <Link
                                                        to={`/product/${item.product}`}
                                                    >
                                                        {item.name}
                                                    </Link>
                                                </Col>
                                                <Col md={4}>
                                                    {item.qty} x ${item.price} =
                                                    ${item.qty * item.price}
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            )}
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant="flush">
                            <ListGroup.Item>
                                <h2>Order Summary</h2>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Items</Col>
                                    <Col>${order.itemsPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Shipping</Col>
                                    <Col>${order.shippingPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax</Col>
                                    <Col>${order.taxPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Total</Col>
                                    <Col>${order.totalPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            {!order.isPaid && (
                                        <ListGroup.Item>
                                            <Button onClick={midtransHandler}>MIDTRANS PAY</Button>
                                        </ListGroup.Item> 
                            )}
                            {loadingDeliver && <Loader />}
                            {userInfo &&
                                userInfo.isAdmin &&
                                order.isPaid &&
                                !order.isDelivered && (
                                    <ListGroup.Item className="d-grid">
                                        <Button
                                            type="button"
                                            className="btn btn-block"
                                            onClick={deliverHandler}
                                        >
                                            Mark As Delivered
                                        </Button>
                                    </ListGroup.Item>
                                )}
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </>
    );
};

export default OrderScreen;
